#!/usr/bin/env python3

#Teste de sequência curta
dna_teste = "AATTGGCCA"

a_count_teste = dna_teste.count("A")
t_count_teste = dna_teste.count("T")
g_count_teste = dna_teste.count("G")
c_count_teste = dna_teste.count("C")

conteudo_at_teste = (a_count_teste + t_count_teste)/len(dna_teste)
conteudo_gc_teste = (g_count_teste + c_count_teste)/len(dna_teste)

print("Conteúdo AT da sequência teste =", conteudo_at_teste)
print("Conteúdo GC da sequência teste =", conteudo_gc_teste)

#Teste real
dna = ""

a_count = dna.count("A")
t_count = dna.count("T")
g_count = dna.count("G")
c_count = dna.count("C")

conteudo_at = (a_count + t_count)/len(dna)
conteudo_gc = (g_count + c_count)/len(dna)

print("Conteúdo AT da sequência =", conteudo_at)
print("Conteúdo GC da sequência =", conteudo_gc)
